import React, { useState } from 'react';
import './App.css'; // 假设我们将样式放在一个单独的CSS文件中

function App() {
    const [isLoginPopupOpen, setLoginPopupOpen] = useState(false);

    const openLoginPopup = () => {
        setLoginPopupOpen(true);
    };

    const closeLoginPopup = () => {
        setLoginPopupOpen(false);
    };

    const sendVerificationCode = () => {
    const phoneNumber = document.getElementById('phoneNumber').value;
    if (phoneNumber) {
        alert(`验证码已发送至: ${phoneNumber}`);
    } else {
        alert('请输入有效的手机号');
        }
    };

    const login = () => {
    const verificationCode = document.getElementById('verificationCode').value;
    if (verificationCode) {
    alert('登录成功！');
    closeLoginPopup();
    } else {
    alert('请输入有效的验证码');
    }
    };

    return (
    <div className="App">
    <div className="navbar">
    <button onClick={openLoginPopup}>登录/注册</button>
    </div>

    {isLoginPopupOpen && (
    <div className="login-popup">
    <div className="login-container">
    <h2>登录</h2>
    <input type="text" id="phoneNumber" placeholder="请输入手机号" />
    <button onClick={sendVerificationCode}>获取验证码</button>
    <input type="text" id="verificationCode" placeholder="请输入验证码" />
    <button onClick={login}>登录</button>
    </div>
    </div>
    )}
    </div>
    );
}

export default App;
